import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import App from './App.js';

function RecipeFinder() {
	const [query, setQuery] = useState('');
	const [recipes, setRecipes] = useState([]);
	const [reviews, setReviews] = useState({});

	const apiKey = 'jPnxqhrP/S7pumtdXpFjfA==pNqU3IWsiB8PY53F';

	const searchRecipes = async () => {
		try {
			const response = await axios.get(`https://api.api-ninjas.com/v1/recipe?query=${query}`, {
				headers: {
					'X-Api-Key': apiKey
				}
			});
			setRecipes(response.data);
		} catch (error) {
			console.error('Error fetching recipes:', error);
		}
	};

	const addReview = (recipeId, review) => {
		setReviews(prevReviews => ({
			...prevReviews,
			[recipeId]: [...(prevReviews[recipeId] || []), review]
		}));
	};

	return (
		<div className="container mt-5">
			<h1 className="mb-4">Recipe Finder</h1>
			<div className="input-group mb-4">
				<input
					type="text"
					className="form-control"
					placeholder="Search for recipes..."
					value={query}
					onChange={(e) => setQuery(e.target.value)}
				/>
				<div className="input-group-append">
					<button onClick={searchRecipes} className="btn btn-primary">Search</button>
				</div>
			</div>
			<div className="row">
				{recipes.map(recipe => (
					<div className="col-md-4 mb-4" key={recipe.id}>
						<div className="card">
							<img src={recipe.image_url || 'https://via.placeholder.com/150'} className="card-img-top" alt={recipe.title} />
							<div className="card-body">
								<h5 className="card-title">{recipe.title}</h5>
								<RecipeReviews recipeId={recipe.id} reviews={reviews[recipe.id]} addReview={addReview} />
							</div>
						</div>
					</div>
				))}
			</div>
		</div>
	);
}

function RecipeReviews({ recipeId, reviews, addReview }) {
	const [newReview, setNewReview] = useState('');

	const handleAddReview = () => {
		if (newReview.trim()) {
			addReview(recipeId, newReview);
			setNewReview('');
		}
	};

	return (
		<div>
			<h6>Reviews:</h6>
			<ul className="list-unstyled">
				{reviews && reviews.map((review, index) => (
					<li key={index} className="mb-2">
						<div className="border p-2">{review}</div>
					</li>
				))}
			</ul>
			<div className="input-group mt-2">
				<input
					type="text"
					className="form-control"
					placeholder="Add a review..."
					value={newReview}
					onChange={(e) => setNewReview(e.target.value)}
				/>
				<div className="input-group-append">
					<button onClick={handleAddReview} className="btn btn-secondary">Add Review</button>
				</div>
			</div>
		</div>
	);
}

export default RecipeFinder;
